import React, { useState } from "react";

function App() {
  const [form, setForm] = useState({
    activityType: "TRANSPORT",
    fuelType: "diesel",
    value: ""
  });

  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitData = async () => {
    // 1Ô∏è‚É£ Call Spring Boot (stores data)
    const springRes = await fetch("http://localhost:8081/api/carbon/calculate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    const springData = await springRes.json();

    // 2Ô∏è‚É£ Call Flask ML (prediction)
    const flaskRes = await fetch("http://localhost:5000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    const flaskData = await flaskRes.json();

    setResult({
      actual: springData.emission,
      predicted: flaskData.predicted_emission,
    });
  };

  return (
    <div style={{ padding: "40px", fontFamily: "Arial" }}>
      <h1>CarbonOS üåç</h1>
      <p>AI-based Carbon Emission Intelligence Platform</p>

      <input
        name="value"
        placeholder="Enter value (e.g. km / units)"
        onChange={handleChange}
      />
      <br /><br />

      <button onClick={submitData}>Calculate Emission</button>

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h3>Results</h3>
          <p>Actual Emission (Rule-based): {result.actual}</p>
          <p>Predicted Emission (ML): {result.predicted}</p>
        </div>
      )}
    </div>
  );
}

export default App;
